mapper.select:
SELECT c.*, t.contract_product_id,t.factory_name,t.product_no,t.product_desc,t.cnumber,t.packing_unit,t.price,t.amount, t.ext_cproduct_id,t.ext_factory_name,t.ext_product_no,t.ext_product_desc,t.ext_cnumber,t.ext_packing_unit,t.ext_price,t.ext_amount
FROM
(SELECT * FROM contract_c) c LEFT JOIN ( SELECT cp.contract_id, cp.contract_product_id,cp.factory_name,cp.product_no,cp.product_desc,cp.cnumber,cp.packing_unit,cp.price,cp.amount, ep.ext_cproduct_id,ep.factory_name AS ext_factory_name,ep.product_no AS ext_product_no,ep.product_desc as ext_product_desc,ep.cnumber as ext_cnumber,ep.packing_unit as ext_packing_unit,ep.price as ext_price,ep.amount as ext_amount FROM ( SELECT cp.contract_product_id,cp.contract_id,f.factory_name,cp.product_no, cp.product_desc,cp.cnumber,cp.packing_unit,cp.price,cp.amount FROM (SELECT contract_product_id,contract_id,factory_id,product_no, product_desc,cnumber,packing_unit, price,amount FROM contract_product_c) cp LEFT JOIN (SELECT factory_id,factory_name FROM factory_c) f ON cp.factory_id = f.factory_id ) cp LEFT JOIN ( SELECT ep.ext_cproduct_id,ep.contract_product_id,f.factory_name,ep.product_no, ep.product_desc,ep.cnumber,ep.packing_unit,ep.price,ep.amount FROM (SELECT ext_cproduct_id,contract_product_id,factory_id,product_no, product_desc,cnumber,packing_unit, price,amount FROM ext_cproduct_c) ep LEFT JOIN (SELECT factory_id,factory_name FROM factory_c) f ON ep.factory_id=f.factory_id ) ep ON cp.contract_product_id=ep.contract_product_id ) t ON c.contract_id=t.contract_id WHERE c.contract_id=#{id}

mapper.update:
update contract_c
OFFEROR=#{offeror}, CONTRACT_NO=#{contractNo}, SIGNING_DATE=#{signingDate}, INPUT_BY=#{inputBy}, CHECK_BY=#{checkBy}, INSPECTOR=#{inspector}, CREQUEST=#{crequest}, CUSTOM_NAME=#{customName}, SHIP_TIME=#{shipTime}, IMPORT_NUM=#{importNum}, DELIVERY_PERIOD=#{deliveryPeriod}, REMARK=#{remark}, PRINT_STYLE=#{printStyle}, OLD_STATE=#{oldState}, STATE=#{state}, OUT_STATE=#{outState}, TRADE_TERMS=#{tradeTerms},
where CONTRACT_ID=#{id}

mapper.delete:
delete from contract_c
where CONTRACT_ID in

	#{pid}

mapper.update:
update contract_c
set state=#{state}
where CONTRACT_ID in 

	#{id}
