mapper.select:
SELECT ( SELECT COUNT(*) FROM contract_product_c WHERE contract_id=c.contract_id ) cpnum, ( SELECT COUNT(*) FROM ext_cproduct_c WHERE contract_product_id IN ( SELECT contract_product_id FROM contract_product_c WHERE contract_id=c.contract_id ) ) epnum, c.* FROM contract_c c
where 1=1
and c.state=#{state}

mapper.update:
update contract_c
OFFEROR=#{offeror}, CONTRACT_NO=#{contractNo}, SIGNING_DATE=#{signingDate}, INPUT_BY=#{inputBy}, CHECK_BY=#{checkBy}, INSPECTOR=#{inspector}, CREQUEST=#{crequest}, CUSTOM_NAME=#{customName}, SHIP_TIME=#{shipTime}, IMPORT_NUM=#{importNum}, DELIVERY_PERIOD=#{deliveryPeriod}, REMARK=#{remark}, PRINT_STYLE=#{printStyle}, OLD_STATE=#{oldState}, STATE=#{state}, OUT_STATE=#{outState}, TRADE_TERMS=#{tradeTerms},
where CONTRACT_ID=#{id}

mapper.delete:
delete from contract_c
where CONTRACT_ID in

	#{pid}

mapper.update:
update contract_c
set state=#{state}
where CONTRACT_ID in 

	#{id}
